@Composable
fun DetailsScreen(
 itemId: String,
 onBack: () -> Unit,
 viewModel: DetailViewModel = hiltViewModel()
) {
 val detailState by viewModel.detailData.collectAsStateWithLifecycle()
 val snackbarHostState = remember { SnackbarHostState() }

 LaunchedEffect(itemId) {
 viewModel.callDetailApi(itemId)
 }

 ProductDetailsState(
 state = detailState,
 snackbarHostState = snackbarHostState,
 onBackClick = onBack
 )
}


@Composable
fun ProductDetailsState(
 state: NetworkRequest<ResponseDetail>,
 snackbarHostState: SnackbarHostState,
 onBackClick: () -> Unit
) {
 when (state) {
 is NetworkRequest.Loading -> ProductDetailsContent(isLoading = true)
 is NetworkRequest.Success -> state.data?.data?.let { data ->
 ProductDetailsContent(
 isLoading = false,
 data = data,
 onBackClick = onBackClick
 )
 }

 is NetworkRequest.Error -> {
 ShowErrorIfNeeded(
 isError = true,
 errorMessage = state.error.orEmpty(),
 snackbarHostState = snackbarHostState
 )
 ProductDetailsContent(isLoading = true)
 }
 }
}

