@Composable
fun HeaderImagesSlider(
 imageUrls: List<String>,
 modifier: Modifier = Modifier
) {
 var selectedIndex by remember { mutableIntStateOf(0) }

 LaunchedEffect(imageUrls) {
 selectedIndex = 0
 }

 Column(
 verticalArrangement = Arrangement.Top,
 horizontalAlignment = Alignment.CenterHorizontally,
 modifier = modifier
 .fillMaxWidth()
 .wrapContentHeight()
 .background(White)
 ) {
 LoadImage(
 url = imageUrls[selectedIndex],
 modifier = Modifier
 .aspectRatio(1.0f)
 .padding(bottom = 16.dp)
 )

 LazyRow(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp),
 horizontalArrangement = Arrangement.spacedBy(10.dp)
 ) {
 items(imageUrls.size) { index ->
 Box(
 modifier = Modifier
 .size(62.dp)
 .border(
 color = if (index == selectedIndex) Green else Cadet_Grey,
 width = 2.dp,
 shape = RoundedCornerShape(16.dp)
 )
 .clickable { selectedIndex = index },
 contentAlignment = Alignment.Center
 ) {
 LoadImage(
 url = imageUrls[index],
 contentDescription = "",
 modifier = Modifier
 .size(50.dp)
 .clip(RoundedCornerShape(12.dp))
 )
 }
 }
 }
 }
}


@Composable
fun ProductItemColor(
 colors: List<ResponseDetail.Data.Color>,
 selectedColor: String,
 onColorSelected: (String) -> Unit
) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp)
 ) {
 Text(
 text = stringResource(R.string.title_colors),
 color = Eerie_Black,
 style = MaterialTheme.typography.titleLarge,
 )

 Spacer(modifier = Modifier.height(6.dp))

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(16.dp),
 contentPadding = PaddingValues(horizontal = 8.dp)
 ) {
 items(colors) { colorObj ->
 val hex = colorObj.colorValue ?: "#00FFFFFF"
 val color = try {
 Color(hex.toColorInt())
 } catch (e: Exception) {
 Color.White
 }

 val isSelected = colorObj.colorName == selectedColor

 val animatedSize by animateDpAsState(
 targetValue = if (isSelected) 44.dp else 36.dp,
 animationSpec = spring(
 dampingRatio = Spring.DampingRatioMediumBouncy,
 stiffness = Spring.StiffnessLow
 ),
 label = "color size animation"
 )

 val animatedElevation by animateDpAsState(
 targetValue = if (isSelected) 6.dp else 0.dp,
 animationSpec = tween(durationMillis = 250),
 label = "elevation animation"
 )

 Box(
 modifier = Modifier
 .size(animatedSize)
 .shadow(animatedElevation, CircleShape, clip = false)
 .clip(CircleShape)
 .background(color)
 .border(
 width = if (isSelected) 2.dp else 1.dp,
 color = if (isSelected) Green else Color.LightGray,
 shape = CircleShape
 )
 .clickable {
 colorObj.colorName?.let { onColorSelected(it) }
 }
 )
 }
 }
 }
}

